package com.example.recyclerviewexample

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.widget.Toast
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.google.android.material.floatingactionbutton.FloatingActionButton

class MainActivity : AppCompatActivity(), MyAdapter.OnListItemClickListener {

    private lateinit var recyclerView: RecyclerView
    private lateinit var myAdapter: MyAdapter
    private lateinit var buttonAdd: FloatingActionButton

    val onClick: (User) -> Unit = {
        Toast.makeText(this,"$it",Toast.LENGTH_LONG).show()
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        myAdapter = MyAdapter()
        myAdapter.setData(getItems())
        myAdapter.listener = this
        myAdapter.listener1 = onClick

        recyclerView = findViewById(R.id.recyclerView)
        buttonAdd = findViewById(R.id.floatingActionButtonAdd)
        buttonAdd.setOnClickListener {
            myAdapter.addItem(User("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQdAAy3sYaw8sYiwaLU8BmJK0Qn0idbXAUQow&usqp=CAU","FirstName3","LastName3"))
        }

        recyclerView.layoutManager = LinearLayoutManager(this, RecyclerView.VERTICAL,false)
        recyclerView.adapter = myAdapter



    }


    private fun getItems(): List<User>{
        return listOf(
            User("https://chudo.tech/wp-content/uploads/2019/05/fXwPgEl28SA.jpg","FirstName1","LastName1"),
            User("data:image/jpeg;base64,","FirstName2","LastName"),
            User("https://www.ixbt.com/img/n1/news/2021/9/2/cfaulkner_211004_4791_00011.0_large.jpg","FirstNam e3","LastName3"),
            User("https://hs-store.ru/upload/medialibrary/e61/e61b89480e78efe58118fcbfca3cdc42.jpg","FirstName4","LastName4"),
            User("https://upload.wikimedia.org/wikipedia/commons/thumb/b/b8/Android.JPG/250px-Android.JPG","FirstName5","LastName5"),
            User("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQdAAy3sYaw8sYiwaLU8BmJK0Qn0idbXAUQow&usqp=CAU","FirstName6","LastName6"),
        )
    }

    override fun onClick(user: User) {
        Toast.makeText(this,"$user",Toast.LENGTH_LONG).show()
    }


}